(function() {
  var Casper, casper, colorizer;

  Casper = require('casper').Casper;

  colorizer = require('colorizer').create('Colorizer');

  casper = new Casper({
    clientScripts: ['spec/jquery.min.js']
  });

  Casper.prototype.capture_resource = function(name) {
    console.log(colorizer.colorize("  âœ“", 'INFO') + (" " + name + "()"));
    return this.captureSelector("spec/results/" + name + ".png", "." + name);
  };

  Casper.prototype.headline = function(name) {
    this.echo('');
    this.echo('----------------------');
    this.echo("" + name, 'WARNING');
    this.echo('----------------------');
    return this.echo('');
  };

  casper.start('http://localhost:3000', function() {});

  casper.then(function() {
    this.headline('buttons');
    this.capture_resource('button');
    this.capture_resource('button-color');
    this.capture_resource('button-small');
    this.capture_resource('button-medium');
    this.capture_resource('button-large');
    this.capture_resource('button-custom-size');
    this.capture_resource('button-disabled');
    this.capture_resource('simple-button');
    this.capture_resource('simple-color');
    this.capture_resource('simple-size');
    return this.capture_resource('simple-disabled');
  });

  casper.then(function() {
    this.headline('code');
    this.capture_resource('code');
    return this.capture_resource('pre');
  });

  casper.then(function() {
    this.headline('forms');
    this.capture_resource('input');
    this.capture_resource('input-search');
    this.capture_resource('input-disabled');
    this.capture_resource('input-error');
    this.capture_resource('input-warning');
    this.capture_resource('input-success');
    this.capture_resource('field');
    this.capture_resource('field-error');
    this.capture_resource('field-warning');
    return this.capture_resource('field-success');
  });

  casper.then(function() {
    this.headline('gradients');
    this.capture_resource('gradient');
    this.capture_resource('gradient-multiple-stops');
    this.capture_resource('gradient-mixin');
    this.capture_resource('simple-gradient');
    this.capture_resource('noise-gradient');
    return this.capture_resource('simple-noise-gradient');
  });

  casper.then(function() {
    return this.echo("");
  });

  casper.run();

}).call(this);
